// .bmad/integrations/jira-sync.ts
import { JiraClient } from '@bmad/jira-integration';
import { StoryManager } from '@bmad/core';

export class JiraSync {
  private jiraClient: JiraClient;
  private storyManager: StoryManager;

  constructor() {
    this.jiraClient = new JiraClient({
      url: process.env.JIRA_URL!,
      email: process.env.JIRA_EMAIL!,
      token: process.env.JIRA_API_TOKEN!,
    });
    this.storyManager = new StoryManager();
  }

  /**
   * Sync a BMAD story to Jira
   */
  async syncStoryToJira(storyId: string) {
    // Get BMAD story
    const story = await this.storyManager.getStory(storyId);

    // Check if Jira issue already exists
    const jiraIssue = await this.jiraClient.findIssueByBmadId(storyId);

    if (jiraIssue) {
      // Update existing issue
      await this.jiraClient.updateIssue(jiraIssue.key, {
        summary: story.title,
        description: story.description,
        status: this.mapBmadStatusToJira(story.status),
        customFields: {
          storyPoints: story.points,
          epicLink: story.epicId,
        },
      });
    } else {
      // Create new issue
      const newIssue = await this.jiraClient.createIssue({
        project: process.env.JIRA_PROJECT!,
        issueType: 'Story',
        summary: story.title,
        description: story.description,
        customFields: {
          bmadId: storyId,
          storyPoints: story.points,
          epicLink: story.epicId,
        },
      });

      // Update BMAD story with Jira key
      await this.storyManager.updateStory(storyId, {
        jiraKey: newIssue.key,
      });
    }
  }

  /**
   * Sync all BMAD stories to Jira
   */
  async syncAllStories() {
    const stories = await this.storyManager.getAllStories();

    for (const story of stories) {
      await this.syncStoryToJira(story.id);
    }
  }

  /**
   * Listen for Jira webhooks and update BMAD
   */
  async handleJiraWebhook(webhook: any) {
    const issue = webhook.issue;
    const bmadId = issue.fields.customfield_bmad_id;

    if (bmadId) {
      await this.storyManager.updateStory(bmadId, {
        status: this.mapJiraStatusToBmad(issue.fields.status.name),
        assignee: issue.fields.assignee?.displayName,
        updatedAt: new Date(issue.fields.updated),
      });
    }
  }

  private mapBmadStatusToJira(status: string): string {
    const mapping: Record<string, string> = {
      todo: 'To Do',
      in_progress: 'In Progress',
      in_review: 'In Review',
      done: 'Done',
    };
    return mapping[status] || 'To Do';
  }

  private mapJiraStatusToBmad(status: string): string {
    const mapping: Record<string, string> = {
      'To Do': 'todo',
      'In Progress': 'in_progress',
      'In Review': 'in_review',
      'Done': 'done',
    };
    return mapping[status] || 'todo';
  }
}

// .bmad/integrations/github-integration.ts
import { Octokit } from '@octokit/rest';
import { StoryManager } from '@bmad/core';

export class GitHubIntegration {
  private octokit: Octokit;
  private storyManager: StoryManager;

  constructor() {
    this.octokit = new Octokit({
      auth: process.env.GITHUB_TOKEN,
    });
    this.storyManager = new StoryManager();
  }

  /**
   * Create PR for a story
   */
  async createPRForStory(storyId: string) {
    const story = await this.storyManager.getStory(storyId);

    // Create branch
    const branchName = `feature/${storyId}-${this.sanitize(story.title)}`;
    await this.createBranch(branchName);

    // Get PR template
    const prBody = await this.generatePRBody(story);

    // Create PR
    const pr = await this.octokit.pulls.create({
      owner: process.env.GITHUB_OWNER!,
      repo: process.env.GITHUB_REPO!,
      title: `[${storyId}] ${story.title}`,
      head: branchName,
      base: 'main',
      body: prBody,
      draft: false,
    });

    // Add labels
    await this.octokit.issues.addLabels({
      owner: process.env.GITHUB_OWNER!,
      repo: process.env.GITHUB_REPO!,
      issue_number: pr.data.number,
      labels: ['bmad-generated', `story-${storyId}`],
    });

    // Request reviews
    await this.octokit.pulls.requestReviewers({
      owner: process.env.GITHUB_OWNER!,
      repo: process.env.GITHUB_REPO!,
      pull_number: pr.data.number,
      reviewers: ['tech-lead', 'senior-dev'],
    });

    // Update story with PR link
    await this.storyManager.updateStory(storyId, {
      prUrl: pr.data.html_url,
      prNumber: pr.data.number,
    });

    return pr.data;
  }

  /**
   * Generate PR body from story
   */
  private async generatePRBody(story: any): Promise<string> {
    return `
## Story: ${story.id} - ${story.title}

### Description
${story.description}

### Acceptance Criteria
${story.acceptanceCriteria.map((ac: string, i: number) => `${i + 1}. ${ac}`).join('\n')}

### Testing
- [ ] Unit tests added/updated
- [ ] Integration tests added/updated
- [ ] Manual testing completed

### Checklist
- [ ] Code follows style guidelines
- [ ] Self-review completed
- [ ] Comments added for complex logic
- [ ] Documentation updated
- [ ] No new warnings generated

### Related
- Story: ${story.id}
- Epic: ${story.epicId}
${story.jiraKey ? `- Jira: ${story.jiraKey}` : ''}
    `.trim();
  }

  /**
   * Listen for GitHub webhooks
   */
  async handleGitHubWebhook(webhook: any) {
    if (webhook.action === 'closed' && webhook.pull_request.merged) {
      // PR merged - update story status
      const storyId = this.extractStoryId(webhook.pull_request.title);
      if (storyId) {
        await this.storyManager.updateStory(storyId, {
          status: 'done',
          completedAt: new Date(),
        });
      }
    }
  }

  private sanitize(text: string): string {
    return text
      .toLowerCase()
      .replace(/[^a-z0-9]+/g, '-')
      .replace(/^-|-$/g, '');
  }

  private extractStoryId(title: string): string | null {
    const match = title.match(/\[(\d+\.\d+)\]/);
    return match ? match[1] : null;
  }

  private async createBranch(branchName: string) {
    const { data: ref } = await this.octokit.git.getRef({
      owner: process.env.GITHUB_OWNER!,
      repo: process.env.GITHUB_REPO!,
      ref: 'heads/main',
    });

    await this.octokit.git.createRef({
      owner: process.env.GITHUB_OWNER!,
      repo: process.env.GITHUB_REPO!,
      ref: `refs/heads/${branchName}`,
      sha: ref.object.sha,
    });
  }
}

// .bmad/integrations/slack-notifier.ts
import { WebClient } from '@slack/web-api';

export class SlackNotifier {
  private client: WebClient;

  constructor() {
    this.client = new WebClient(process.env.SLACK_BOT_TOKEN);
  }

  /**
   * Send notification to Slack
   */
  async notify(channel: string, message: string, blocks?: any[]) {
    await this.client.chat.postMessage({
      channel,
      text: message,
      blocks: blocks || [
        {
          type: 'section',
          text: {
            type: 'mrkdwn',
            text: message,
          },
        },
      ],
    });
  }

  /**
   * Notify workflow completion
   */
  async notifyWorkflowComplete(workflowName: string, storyId: string) {
    await this.notify(
      '#bmad-alerts',
      `âœ… Workflow *${workflowName}* completed for story *${storyId}*`,
      [
        {
          type: 'section',
          text: {
            type: 'mrkdwn',
            text: `*Workflow Completed* âœ…\n\nWorkflow: *${workflowName}*\nStory: *${storyId}*\nCompleted at: ${new Date().toLocaleString()}`,
          },
        },
        {
          type: 'actions',
          elements: [
            {
              type: 'button',
              text: {
                type: 'plain_text',
                text: 'View Story',
              },
              url: `https://your-bmad-url/stories/${storyId}`,
            },
          ],
        },
      ]
    );
  }

  /**
   * Notify PR created
   */
  async notifyPRCreated(prTitle: string, prUrl: string, author: string) {
    await this.notify(
      '#code-reviews',
      `ğŸ“ New PR: *${prTitle}*`,
      [
        {
          type: 'section',
          text: {
            type: 'mrkdwn',
            text: `*New Pull Request* ğŸ“\n\nTitle: *${prTitle}*\nAuthor: ${author}\nCreated: ${new Date().toLocaleString()}`,
          },
        },
        {
          type: 'actions',
          elements: [
            {
              type: 'button',
              text: {
                type: 'plain_text',
                text: 'Review PR',
              },
              url: prUrl,
              style: 'primary',
            },
          ],
        },
      ]
    );
  }
}

// Jenkinsfile
pipeline {
  agent any

  environment {
    BMAD_TOKEN = credentials('bmad-token')
  }

  stages {
    stage('Checkout') {
      steps {
        checkout scm
      }
    }

    stage('BMAD Story Context') {
      steps {
        script {
          // Extract story ID from branch name
          def storyId = env.BRANCH_NAME.replaceAll(/feature\/(\d+\.\d+).*/, '$1')

          // Load story context
          sh "bmad run story-context --story=${storyId}"
        }
      }
    }

    stage('Build') {
      steps {
        sh 'npm install'
        sh 'npm run build'
      }
    }

    stage('Test') {
      steps {
        sh 'npm run test'
      }
    }

    stage('BMAD Code Review') {
      steps {
        sh 'bmad run code-review --auto'
      }
    }

    stage('Deploy') {
      when {
        branch 'main'
      }
      steps {
        sh 'npm run deploy'
      }
    }
  }

  post {
    success {
      script {
        // Notify BMAD of successful build
        sh 'bmad notify success --build=${BUILD_NUMBER}'
      }
    }
    failure {
      script {
        // Notify BMAD of failed build
        sh 'bmad notify failure --build=${BUILD_NUMBER}'
      }
    }
  }
}
